public class lisStack {
    static class node
    {
        T data;
        node next;
        public node() {    
        }
        public node(T value)
        {
            this.data=value;
        }
    }
    int length;
    node head;//头节点
    public lisStack() {
        head=new node<>();
        length=0;
    }
    boolean isEmpty()
    {
        return head.next==null;
    }
    int length()
    {
        return length;
    }
    public void push(T value) {//近栈
       node team=new node(value);
       if(length==0)
       {
           head.next=team;
       }
       else {
        team.next=head.next;
        head.next=team;}
       length++;
    }
    public T peek() throws Exception {
        if(length==0) {throw new Exception("链表为空");}
        else {//删除
            return (T) head.next.data;
        }
  }
    public T pop() throws Exception {//出栈
          if(length==0) {throw new Exception("链表为空");}
          else {//删除
            T value=(T) head.next.data;
            head.next=head.next.next;//va.next
            length--;
            return value;


        }
    }
    public String toString(){
        if(length==0) {return "";}
        else {
            String va="";
            node team=head.next;
            while(team!=null)
            {
                va+=team.data+" ";
                team=team.next;
            }
            return va;
        }

    }
}

